com.cloudbees.plugins.flow.BuildFlow.dsl:
parallel (
  {
    parallel (
     { windows_debug_tests = build("dotnet_corefx_windows_debug") },
     { linux_debug_libs = build("dotnet_corefx_linux_debug") },
     { osx_debug_libs = build("dotnet_corefx_mac_debug") }
    )

    linux_debug_libs_snippet = "<SpecificBuildSelector><buildNumber>$linux_debug_libs.build.number</buildNumber></SpecificBuildSelector>"
    osx_debug_libs_snippet = "<SpecificBuildSelector><buildNumber>$osx_debug_libs.build.number</buildNumber></SpecificBuildSelector>"
    windows_debug_tests_snippet = "<SpecificBuildSelector><buildNumber>$windows_debug_tests.build.number</buildNumber></SpecificBuildSelector>"

    build("dotnet_corefx_linux_debug_tst",
          COREFX_LINUX_BUILD: linux_debug_libs_snippet, 
          COREFX_WINDOWS_BUILD: windows_debug_tests_snippet)
  },
  {
    parallel (
      { build("dotnet_corefx_windows_release") },
      { build("dotnet_corefx_linux_release") },
      { build("dotnet_corefx_mac_release") }
    )
  }
)
